import React,{Component} from 'react';
import '../AdminCss/admintecherrecord.css';
import {Link} from 'react-router-dom';
import 'styled-components/dist/styled-components';
import SideNavbar from '../../SideNavMenu';
import $ from 'jquery';




class AdminTeacherRecord extends Component{
    constructor(props){
        super(props);
        
        this.state={
            teachers:[],
            
        }
    }
    componentDidMount(){

      $(function(){
        var table = '#page'

        $('#maxRows').on('change', function () {

            $('.pagination').html('')

            var trnum = 0;

            var maxrow = parseInt($(this).val());
            console.log(maxrow);
            var totalrow = $(table + ' tbody tr').length;
            $(table + ' tr:gt(0)').each(function () {

                trnum++;
                if (trnum > maxrow) {
                    $(this).hide();

                }
                if (trnum <= maxrow) {
                    $(this).show();
                }

            })
            if (totalrow > maxrow) {
                var pagenum = Math.ceil(totalrow / maxrow);
                for (var i = 1; i <= pagenum;) {



                    $('.pagination').append('<li class="page-item" data-page="' + i + '">' + '<a href="#" class="page-link" >' + i++ + '</a></li>').show();
                }
            }
            $('.pagination li:first-child').addClass('active');
            $('.pagination li').on('click', function () {
                var pageNum = $(this).attr('data-page');
                var trIndex = 0;
                $('.pagination li').removeClass('active');
                $(this).addClass('active');
                $(table + ' tr:gt(0)').each(function () {
                    trIndex++;
                    if (trIndex > (maxrow * pageNum) || trIndex <= ((maxrow * pageNum) - maxrow)) {
                        $(this).hide()

                    } else {
                        $(this).show()
                    }

                });

            });

        });

        $(".search_input").on("keyup", function () {

            $("#noData").remove();
            var value = $(this).val();
            var patt = new RegExp(value, "i");
            var sw = 0;
            var counter = 0;
            $('.teachers_table').find('tr').each(function () {
                counter++;
                if (!($(this).find('td').text().search(patt) > -1)) {
                    $(this).hide();
                    sw++;
                } else if (($(this).find('td').text().search(patt) > -1)) {
                    $(this).show();
                }
            });
            if (sw == counter) {
                $(".teachers_table").append(`<tr id="noData">
                <td colspan="6" class='text-center bg-info text-white'>No Record Found</td>
              </tr>`);
            } else {
                $("#noData").remove();
            }



        });
  

      });
        const teacher_url = "https://jsonplaceholder.typicode.com/posts";
        
        fetch(teacher_url,{
            method:"GET"
        })
            .then((response)=>{
                return response.json();
            })
            .then((data)=>{
                this.setState({
                    teachers : data
                });
                });
          
    }
    render() {

      
      let teachers = this.state.teachers;
      let table_teachers = teachers.map((teachers, item) =>

          <tr key={item}>
              <td>{teachers.userId}</td>
              <td>{teachers.title}</td>
              <td>{teachers.id}</td>
              <td>{teachers.userId}</td>
              <td>{teachers.userId}</td>
              <td>{teachers.id}</td>
          </tr>
      );

        // const columns = [
