        //             return (
        //                 <button class='btn btn-danger'>Delete</button>
        //             )
        //         },
        //         style: {
        //             textAlign: 'center'
        //         },
        //         width: 150,
        //         filterable: false
        //     }
        // ]
        return(
          <div>
             <SideNavbar/>
             <div style={{paddingTop:'40px',paddingLeft:'60px',paddingRight:'20px'}}>
             <div class='row '>
                <div class='col-xl-8'>
                    <p class='text' >Teacher Records</p>
                </div>
                <div class='col-xl-4' > 
                <div style={{textAlign:'right',paddingRight:'10px', paddingTop:'10px' }}>
                  <Link to='adminaddteacher'>  
                  <button class='btn btn-primary' >Add Teacher</button></Link>
                </div>
                </div>
                </div>
            
           <div class='col-l-12 reacttable' >  
           <div className='row'>

                        <div className='col-xl-6 col-sm-12'>
                            <label>Select Number of Row</label>
                            <div className='form-group'>
                                <select name='state' id="maxRows" className='form-control' style={{ width: 150 }}>
                                    <option value='10000'>show all</option>
                                    <option vlaue='5'>5</option>
                                    <option vlaue='10'>10</option>
                                    <option vlaue='15'>15</option>
                                    <option value='20'>20</option>
                                </select>
                            </div>
                        </div>
                        <div className='col-xl-6 col-sm-12 d-flex justify-content-xl-end mt-4'>
                            <form>
                                <div className="input-group">
                                    <input type="search" placeholder="Search Here..." className="form-control search_input" />

                                    <div className="input-group-append">
                                        <div className="input-group-text bg-primary">
                                            <div className="fas fa-search text-white"></div>
                                        </div>
                                    </div>
                                </div>
                            </form>
                        </div>
                        </div>

                        <table className='table table-bordered table-striped table-sm-responsive mt-2' id="page">
                            <thead>
                                <tr>
                                    <th>Id</th>
                                    <th>Teacher Name</th>
                                    <th>Subject Name</th>
                                    <th>Class</th>
                                    <th>Year</th>
                                    <th>Address</th>
                                </tr>
                            </thead>
                            <tbody className='teacher_table'>

                                {table_teachers}

                            </tbody>
                        </table>

                        <div className='pagination-container'>
                            <nav>
                                <ul className='pagination'></ul>
                            </nav>
                        </div>
               </div>
               </div>
             </div>
