                                    <th>Class</th>
                                    <th>Year</th>
                                    <th>Address</th>
                                </tr>
                            </thead>
                            <tbody className='parent_table'>

                                {table_parents}


                            </tbody>
                        </table>

                        <div className='pagination-container'>
                            <nav >
                                <ul className='pagination'></ul>
