import React, { Component } from 'react';
import ReactTable from 'react-table';
import 'react-table/react-table.css';
import '../AdminCss/adminparent.css';
import { Link } from 'react-router-dom';
import SideNav from "../../SideNavMenu";
import $ from 'jquery';

class AdminParent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            parents: []
        }
    }

    componentDidMount() {



        $(function () {
            var table = '#page'

            $('#maxRows').on('change', function () {

                $('.pagination').html('')

                var trnum = 0;

                var maxrow = parseInt($(this).val());
                console.log(maxrow);
                var totalrow = $(table + ' tbody tr').length;
                $(table + ' tr:gt(0)').each(function () {

                    trnum++;
                    if (trnum > maxrow) {
                        $(this).hide();

                    }
                    if (trnum <= maxrow) {
                        $(this).show();
                    }

                })
                if (totalrow > maxrow) {
                    var pagenum = Math.ceil(totalrow / maxrow);
                    for (var i = 1; i <= pagenum;) {



                        $('.pagination').append('<li class="page-item" data-page="' + i + '">' + '<a href="#" class="page-link" >' + i++ + '</a></li>').show();
                    }
                }
                $('.pagination li:first-child').addClass('active');
                $('.pagination li').on('click', function () {
                    var pageNum = $(this).attr('data-page');
                    var trIndex = 0;
                    $('.pagination li').removeClass('active');
                    $(this).addClass('active');
                    $(table + ' tr:gt(0)').each(function () {
                        trIndex++;
                        if (trIndex > (maxrow * pageNum) || trIndex <= ((maxrow * pageNum) - maxrow)) {
                            $(this).hide()

                        } else {
                            $(this).show()
                        }

                    });

                });

            });

            $(".search_input").on("keyup", function () {

                $("#noData").remove();
                var value = $(this).val();
                var patt = new RegExp(value, "i");
                var sw = 0;
                var counter = 0;
                $('.parent_table').find('tr').each(function () {
                    counter++;
                    if (!($(this).find('td').text().search(patt) > -1)) {
                        $(this).hide();
                        sw++;
                    } else if (($(this).find('td').text().search(patt) > -1)) {
                        $(this).show();
                    }
                });
                if (sw == counter) {
                    $(".parent_table").append(`<tr id="noData">
                    <td colspan="6" class='text-center bg-info text-white'>No Record Found</td>
                  </tr>`);
                } else {
                    $("#noData").remove();
                }



            });
        });

        const url = "https://jsonplaceholder.typicode.com/posts";
        fetch(url, {
            method: "GET"
        }).then(res => res.json()).then(parents => {
            this.setState({ parents: parents })
        })
    }

    onDelEvent(id) {
        const index = this.state.parents.findIndex(parents => {
            return parents.id === id;
        })
        this.state.parents.splice(index, 1);
        this.setState({ parents: this.state.parents });
    }

    render() {
               
      let parents = this.state.parents;
      let table_parents = parents.map((parents, item) =>

          <tr key={item}>
              <td>{parents.userId}</td>
              <td>{parents.title}</td>
              <td>{parents.id}</td>
              <td>{parents.userId}</td>
              <td>{parents.userId}</td>
              <td>{parents.id}</td>
          </tr>
      );

        return (
            <div>
                <SideNav />
                <div style={{ paddingTop: '40px', paddingLeft: '60px', paddingRight: '20px' }}>
                    <div className='row ml-2'>
                        <div class='col-xl-6'>
                            <p className='text' >Guardian Records</p>
                        </div>

                        <div className='col-xl-6 d-flex justify-content-md-start justify-content-xl-end' >
                            
                                <Link to='adminaddparent'>
                                    <button className='btn btn-primary'>Add Guardian</button></Link>
                            
                        </div>
                    </div>
                    <div class='col-xl-12 reacttable' >

                        <div className='row'>

                            <div className='col-xl-6 col-sm-12'>
                                <label>Select Number of Row</label>
                                <div className='form-group'>
                                    <select name='state' id="maxRows" className='form-control' style={{ width: 150 }}>
                                        <option value='10000'>show all</option>
                                        <option vlaue='5'>5</option>
                                        <option vlaue='10'>10</option>
                                        <option vlaue='15'>15</option>
                                        <option value='20'>20</option>
                                    </select>
                                </div>
                            </div>
                            <div className='col-xl-6 col-sm-12 d-flex justify-content-xl-end justify-content-md-start mt-4 mt-md-2'>
                                <form>
                                    <div className="input-group">
                                        <input type="search" placeholder="Search Here..." className="form-control search_input" />

                                        <div className="input-group-append">
                                            <div className="input-group-text bg-primary">
                                                <div className="fas fa-search text-white"></div>
                                            </div>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>

                        <table className='table table-bordered table-striped table-sm-responsive mt-2' id="page">
                            <thead>
                                <tr>
                                    <th>Id</th>
                                    <th>Student Name</th>
                                    <th>Father Name</th>
                                    <th>Class</th>
                                    <th>Year</th>
                                    <th>Address</th>
                                </tr>
                            </thead>
                            <tbody className='parent_table'>

                                {table_parents}


                            </tbody>
                        </table>

                        <div className='pagination-container'>
                            <nav>
                                <ul className='pagination'></ul>
                            </nav>
                        </div>

                    </div>

                </div>
            </div>

        )

    }
}


export default AdminParent;
